dark_stylesheet:
QWidget {
                background-color: #303030;
                color: #EAEAEA;
            }

            QPushButton {
                background-color: #2d2d2d;
                color: white;
                border-radius: 20px;
                padding: 10px;
                font-size: 20pt;
                border: 4px solid #c9c0bb;
            }

            QPushButton:hover {
                background-color: #424242;
            }

            QPushButton:pressed {
                background-color: #1e1e1e;
            }

            QLabel {
                color: #EAEAEA;
            }

            QStatusBar {
                background-color: #3C3F41;
                color: #EAEAEA;
            }

light_stylesheet:
QPushButton {
    background-color: #faf9f9;
    border: 1px solid black;
    border-radius: 10px;
    color: #555555;
    font-size: 20pt;
    padding: 10px;
}

QPushButton:hover {
    background-color: #add2c9;
    color: #000000;
}

QMainWindow {
    background-color: #faf9f9;
}

QStatusBar {
    background-color: #d9d9d9;
}

QLabel {
    color: #555555;
    font-size: 24px;
}
